package week1.day2;

public class CheckNumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int val1 = 10;
		
		
		if (val1 > 0)
		{
		System.out.println("The Number is Positive  :"  +  val1);
		
		}
		else if (val1 < 0)
		{
			System.out.println("The Number is Negative  :" + val1);
			
			
		}
		
		else 
		{
			System.out.println("The Number is Zero   :"  + val1);
		}

	}

}
